dev.map:
function(doc) {
  var enFlag = doc.enWord !== undefined && doc.enWord.indexOf(',') > -1;
  var bgFlag = doc.bgWord !== undefined && doc.bgWord.indexOf(',') > -1;     
  if(bgFlag || enFlag){
      emit(doc._id, doc);
  }
}

imagefull.map:
function(doc) {
    if(doc.imageSrc !== false &&
    doc.imageSrc !== null &&
    doc.imageSrc !== undefined){
        emit(doc._id, doc);
    }

}

missing_ssl.map:
function(doc) {
    if(doc.imageSrc !== false &&
    doc.imageSrc !== 'false' &&
    doc.imageSrc !== null &&
    doc.imageSrc.indexOf('http:') > -1
    ){
        emit(doc._id, doc);
    }

}

imageless.map:
function(doc) {
    if(
        doc.imageSrc === false ||
        (doc.imageSrc === undefined && doc.enPart!== undefined)
    ){
        emit(doc._id, doc);
    }

}

db.validate_doc_update:
function (newDoc, oldDoc, userCtx) {
var role = "admins";
if (userCtx.roles.indexOf("admin") === -1 && userCtx.roles.indexOf(role) === -1) {
throw({forbidden : "Only users with role " + role + " or an admin can modify this database."});
}
}

relateless.map:
function(doc) {
  if(
      doc.dePart !== undefined &&
      doc.deRelated === undefined &&
      doc.enRelated === undefined
    ){
      emit(doc._id, doc);
  }
}
